mapper.select:
SELECT DISTINCT orders.id as ordersid, personallocate.taskcategory, personallocate.userinfoid, orders.customerid as customerid, orders.vehicleid as vehicleid, personallocate.id as personallocateid, customer.numbering as customername, customer.contactinfo, orders.miles, vehicle.vehflag, vehicle.inspectiondate, customer.contactadd, orders.ifused, orders.ifclean, orders.ifcheckspare, orders.warrcontent, orders.caritems, orders.valuableobj, orders.ownerdescribtion, vehicle.platenum, orders.bustatusid, bustatus.statename, personallocate.allocatetime, vehicle.vehname, vehicle.carmodel, orders.esdeliverytime from orders left join personallocate on(personallocate.ordersid=orders.id) left join customer on (orders.customerid=customer.id) left join vehicle on (orders.vehicleid = vehicle.id) left join bustatus on (orders.bustatusid = bustatus.id)
userinfoid = #{userinfoid}
and (customer.numbering like concat('%',#{keyworld},'%') or contactinfo like concat('%',#{keyworld},'%') or platenum like concat('%',#{keyworld},'%'))
and allocatetime between concat(#{starttime},' 00:00:00') and concat(#{endtime},' 23:59:59')
and taskcategory = #{category}
and bustatusid != '1'
and bustatusid = #{orderstatus}
order by ${sort} ${order}

SELECT o.id AS ordersid, o.numbering, o.customerid, c.numbering AS customername, c.contactinfo, v.id AS vehicleid, v.vehname, v.carmodel, o.warrcontent, b.id AS bustatusid, b.statename AS busstatus, p.id AS paystatusid, p.statusname AS paystatus, o.esdeliverytime, q.ptotal, m.mtotal, (q.ptotal+m.mtotal) as totalcost FROM orders AS o JOIN customer AS c ON (o.customerid = c.id) JOIN vehicle AS v ON (o.vehicleid = v.id) JOIN bustatus AS b ON (o.bustatusid = b.id) JOIN paystatus AS p ON (o.paystatusid = p.id) JOIN ( SELECT _p.ordersid, SUM(_p.ptotal) AS ptotal FROM ( SELECT u.ordersid, (
IF ( u.receivednum = '0', '0', u.receivednum * p.salesprice ) ) AS ptotal FROM partused AS u JOIN part AS p ON (u.partid = p.id) ) AS _p group by _p.ordersid ) AS q ON (q.ordersid = o.id) JOIN ( SELECT _m.ordersid, sum(_m.totalcost) AS mtotal FROM ( SELECT m.ordersid, m.spenttime * i.itemprice AS totalcost FROM mainprojreg AS m JOIN mainitem AS i ON (m.mainitemid = i.id) ) AS _m group by _m.ordersid ) AS m ON (m.ordersid = o.id)
( c.numbering like concat('%',#{keyworld},'%') or c.contactinfo like concat('%',#{keyworld},'%') )
and o.esdeliverytime between concat(#{starttime},' 00:00:00') and concat(#{endtime},' 23:59:59')
and o.bustatusid=#{bustatusid}
and (o.bustatusid='3' or o.bustatusid='4')
and p.id=#{paystatusid}
and o.id=#{ordersid}
ORDER BY o.id ASC

SELECT u.ordersid, p.partname, u.receivednum, p.salesprice, (
IF ( u.receivednum = '0', '0', u.receivednum * p.salesprice ) ) AS ptotal FROM partused AS u JOIN part AS p ON (u.partid = p.id)
and u.ordersid = #{ordersid}
